var directions = [
'',
'',
'',
'',
''
];

var currentXCoord = 0;
var currentYCoord = 0;

for(var i = 0; i < directions.length; i++) {
	for(var j = 0; j < directions[i].length; j++) {
		currentDir = directions[i].charAt(j);
		
		if (currentDir === 'L') {
			if(currentXCoord - 1 < -1) {
				continue;
			}
			
			currentXCoord--;
		} else if (currentDir === 'R') {
			if(currentXCoord + 1 > 1) {
				continue;
			}
			
			currentXCoord++;
		} else if (currentDir === 'U') {
			if(currentYCoord + 1 > 1) {
				continue;
			}
			
			currentYCoord++;
		} else {
			if(currentYCoord - 1 < -1) {
				continue;
			}
			
			currentYCoord--;
		}
	}
	
	var button = 0;
	
	if(currentXCoord === -1) {
		if(currentYCoord === -1) {
			button = 7;
		} else if(currentYCoord === 0) {
			button = 4;
		} else {
			button = 1;
		}
	} else if(currentXCoord === 0) {
		if(currentYCoord === -1) {
			button = 8;
		} else if(currentYCoord === 0) {
			button = 5;
		} else {
			button = 2;
		}
	} else {
		if(currentYCoord === -1) {
			button = 9;
		} else if(currentYCoord === 0) {
			button = 6;
		} else {
			button = 3;
		}
	}
	
	console.log(button);
}